for (var totalButtons = document.querySelectorAll(".drum").length, i = 0; i < totalButtons; i++)document.querySelectorAll(".drum")[i].addEventListener("click", function () { var a = this.innerHTML; keyOrClick(a), buttonAnimation(a) }); function keyOrClick(a) { switch (a) { case "w": new Audio("sounds/crash.mp3").play(); break; case "a": new Audio("sounds/kick-bass.mp3").play(); break; case "s": new Audio("sounds/snare.mp3").play(); break; case "d": new Audio("sounds/tom-1.mp3").play(); break; case "j": new Audio("sounds/tom-2.mp3").play(); break; case "k": new Audio("sounds/tom-3.mp3").play(); break; case "l": new Audio("sounds/tom-4.mp3").play(); break; default: console.log(a) } } function buttonAnimation(a) { var b = document.querySelector("." + a); b.classList.add("pressed"), setTimeout(function () { b.classList.remove("pressed") }, 100) } document.addEventListener("keypress", function (a) { keyOrClick(a.key), buttonAnimation(a.key) })